import React, { useEffect, useRef } from 'react';

const Time = () => {
  const countersRef = useRef([]);

  useEffect(() => {
    const counter = (id, start, end, duration, speed) => {
      let obj = document.getElementById(id),
        current = start,
        range = end - start,
        increment = end > start ? 1 : -1,
        step = Math.abs(Math.floor((duration / range) / speed)),
        timer = setInterval(() => {
          current += increment;
          obj.textContent = current;
          if ((increment === 1 && current >= end) || (increment === -1 && current <= end)) {
            clearInterval(timer);
          }
        }, step);

      return timer;
    };

    const startCounterOnIntersection = (index) => {
      const options = {
        root: null,
        rootMargin: '0px',
        threshold: 0.5, // Adjust this threshold as needed
      };

      const observer = new IntersectionObserver((entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            // Start the counter animation
            countersRef.current[index].classList.add('animate');
            const timer = counter(`count${index + 1}`, 0, 400, 3000, 0.01);
            countersRef.current[index].timer = timer;
            observer.unobserve(countersRef.current[index]);
          }
        });
      }, options);

      observer.observe(countersRef.current[index]);
    };

    // Start observers for each counter
    for (let i = 0; i < 3; i++) {
      startCounterOnIntersection(i);
    }

    return () => {
      // Clear intervals and stop observers when component unmounts
      countersRef.current.forEach((counter) => {
        clearInterval(counter.timer);
      });
    };
