import React from 'react';
import html2pdf from 'html2pdf.js';

const PrintComponent = () => {
  const printData = () => {
    const body = document.getElementById('VerificationBody').innerHTML;
    const PrintResult = document.getElementById('PrintResult').innerHTML;

    document.getElementById('VerificationBody').innerHTML = PrintResult;

    window.print();

    document.getElementById('VerificationBody').innerHTML = body;

    html2pdf()
      .from(PrintResult)
      .save();
  };

  return (
    <div>
      {/* Your HTML content */}
      <div id="VerificationBody" style={{marginTop:'15rem'}}>

        Lorem ipsum dolor sit amet consectetur, adipisicing elit. Consequatur a, accusamus laboriosam quae mollitia odio exercitationem quibusdam quis vitae et?
      </div>
      <div id="PrintResult">Content to Print</div>

      {/* Button to trigger printData */}
      <button onClick={printData}>Print and Save as PDF</button>
    </div>
  );
};

export default PrintComponent;
