import React, { useEffect } from 'react';
import Typed from 'typed.js';

const Time = () => {
  useEffect(() => {
    // Create a new Typed instance when the component mounts
    const typed = new Typed('#element', {
      strings: ['<span class="hideFont">“<b style="color:red !important;">Drishtee</b> envisions a world where all communities are empowered to achieve shared prosperity.”</span>'],
      typeSpeed: 55,
      loop: true,
    });

    // Cleanup function to destroy the Typed instance when the component unmounts
    return () => {
      typed.destroy();
    };
  }, []); // Empty dependency array ensures the effect runs only once after mount

  return (
    <div style={{marginTop:'9rem'}}>
     <h2 style={{ textAlign: 'center', textShadow: '0px 1px 0px maroon' }} className="py-4 text-danger">
        Features And Updates
      </h2>
      <div className="hideFont fw-medium" id="FeatureTextFirst">
        {/* Element to contain animated typing */}
        <span id="element"></span>
      </div>
    </div>
  );
};

export default Time;
