import React, { useState } from 'react';

const MyForm = () => {
    const [isChecked, setIsChecked] = useState(false);

    const handleCheckboxChange = () => {
        setIsChecked(!isChecked);
    };
    const [password, setPassword] = useState('');
    const [showPassword, setShowPassword] = useState(false);

    const togglePasswordVisibility = () => {
        setShowPassword(!showPassword);
    };

    return (
        <form>
            <label htmlFor="password">Password:</label>
            <div class="input-group mb-3">
                <input
                    type={showPassword ? 'text' : 'password'}
                    class="form-control" id="deleteStudent"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <span class="input-group-text bg-warning" id="basic-addon2"><button className='btn  btn-sm' type="button" onClick={togglePasswordVisibility}>
                    {showPassword ? 'Hide' : 'Show'} Password
                </button></span>
            </div>

            <input
                type="checkbox"
                className="form-check-input"
                id="exampleCheck1"
                onChange={handleCheckboxChange}
            />
            <br />



           

            <button
                type="button"
                className={`btn btn-primary ${isChecked ? '' : 'disabled'}`}
                disabled={!isChecked}
            >
                Save
            </button>
        </form>
    );
};

export default MyForm;
