import React, { useEffect } from 'react';

const YourComponent = () => {
    useEffect(() => {
        const loadReCaptcha = () => {
            const script = document.createElement('script');
            script.src = 'https://www.google.com/recaptcha/api.js';
            script.async = true;
            script.defer = true;
            document.head.appendChild(script);
        };

        loadReCaptcha();

        return () => {
            // Cleanup if needed
            document.head.removeChild(document.head.lastChild);
        };
    }, []);

    const handleSubmit = (e) => {
        e.preventDefault();
        // Add your form submission logic here
    };

    useEffect(() => {
        // Make sure to include your onloadCallback function here
        window.onloadCallback = function () {
            window.grecaptcha.render('g-recaptcha', {
                'sitekey': '6LcnjRwpAAAAANRDtbBwSwzFA4sKKBWZdT8iZjlp'
            });
        };
    }, []);

    return (
        <div style={{marginTop:'6rem'}}>
            <form onSubmit={handleSubmit}>
                {/* ... (your form fields) ... */}
                <div className="g-recaptcha" id="g-recaptcha" data-sitekey="6LeSkxwpAAAAAJr0__9WFMn2k3bJ9EW1eT52aaqm"></div>
                {/* Replace YOUR_SITE_KEY with the site key you obtained from reCAPTCHA registration */}
                <button type="submit" className="btn btn-primary">Sign in</button>
            </form>
        </div>
    );
};

export default YourComponent;
