import React, { useEffect } from 'react';

const Time = () => {
  useEffect(() => {
    const counter = (id, start, end, duration) => {
      let obj = document.getElementById(id),
        current = start,
        range = end - start,
        increment = end > start ? 1 : -1,
        step = Math.abs(Math.floor(duration / range)),
        timer = setInterval(() => {
          current += increment;
          obj.textContent = current;
          if (current === end) {
            clearInterval(timer);
          }
        }, step);
    };

    counter("count1", 0, 400, 3000);
    counter("count2", 100, 50, 2500);
    counter("count3", 0, 40, 3000);

    // Cleanup function to clear intervals when the component unmounts
    return () => {
      clearInterval(timer1);
      clearInterval(timer2);
      clearInterval(timer3);
    };
  }, []);

  return (
    <section className="bg-dark text-white py-5 min-vh-100">
      <div className="container">
        <div className="row justify-content-center text-center">
          <div className="col-12">
            <h1 className="pb-3">Number counter animation in React</h1>
          </div>
          <div className="col-md-4">
            <span id="count1" className="display-4"></span>
          </div>
          <div className="col-md-4">
            <span id="count2" className="display-4"></span>
          </div>
          <div className="col-md-4">
            <span id="count3" className="display-4"></span>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Time;
