import React, { useState } from 'react';

const ShowElementOnClick = () => {
 
  // Function to toggle the visibility of the element
  const toggleElement = () => {
    setElementVisibility(!isElementVisible);
  };

  return (
    <div style={{ margin: '4.3rem 1rem' }}>
      {/* Button to toggle the visibility of the element */}
      <button onClick={toggleElement}>
        {isElementVisible ? 'Hide Element' : 'Show Element'}
      </button>

      {/* Element that is conditionally rendered based on state */}
      {isElementVisible && (
        <div>
          <p>This is the element that will be shown or hidden.</p>
          {/* Add more content for the element here */}
        </div>
      )}
    </div>
  );
};

export default ShowElementOnClick;
