import React, { useEffect, useRef } from 'react';

const NumberCounterSection = () => {
  const countersRef = useRef([]);

  useEffect(() => {
    const counter = (id, start, end, duration, speed) => {
      let obj = document.getElementById(id),
        current = start,
        range = end - start,
        increment = end > start ? 1 : -1,
        step = Math.abs(Math.floor((duration / range) / speed)),
        timer = setInterval(() => {
          current += increment;
          obj.textContent = current;
          if ((increment === 1 && current >= end) || (increment === -1 && current <= end)) {
            clearInterval(timer);
          }
        }, step);

      return timer;
    };

    const handleScroll = () => {
      const windowHeight = window.innerHeight;

      countersRef.current.forEach((counter, index) => {
        const boundingBox = counter.getBoundingClientRect();

        // Check if the counter is at least 50% visible
        if (boundingBox.top <= windowHeight * 0.5) {
          // Start the counter animation
          counter.classList.add('animate');
          const timer = counter(`count${index + 1}`, 0, 200, 6000, 1);
          countersRef.current[index].timer = timer;
        }
      });
    };

    // Attach the scroll event listener
    window.addEventListener('scroll', handleScroll);

    return () => {
      // Clear intervals when the component unmounts
      countersRef.current.forEach((counter) => {
        clearInterval(counter.timer);
      });

      // Remove the scroll event listener
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <section className="bg-dark text-white py-5 min-vh-100">
      <div className="container">
        <div className="row justify-content-center text-center">
          <div className="col-12">
            <h1 className="pb-3">Number counter animation in React</h1>
          </div>
          <div className="col-md-4 counter" ref={(el) => (countersRef.current[0] = el)}>
            <span id="count1" className="display-4">0</span>
          </div>
          <div className="col-md-4 counter" ref={(el) => (countersRef.current[1] = el)}>
            <span id="count2" className="display-4">0</span>
          </div>
          <div className="col-md-4 counter" ref={(el) => (countersRef.current[2] = el)}>
            <span id="count3" className="display-4">0</span>
          </div>
        </div>
      </div>
    </section>
  );
};

export default NumberCounterSection;
