import { useState } from 'react';
import { toast } from 'react-toastify';
import AdmissionBody from './Admission/AdmissionBody';
// import AdmissionFooter from './Admission/AdmissionFooter';
// import AdmissionHeader from './Admission/AdmissionHeader';
const AdmissionForm = ((props) => {

    const postForm = () => {
        fetch('http://localhost:3000/studentRegistration', {
            method: 'post',
            headers: {
                "Content-type": "application/json",
            },
            body: JSON.stringify({
                photo, name, fatherName, motherName, gender, address, mobileNumber, dob, course, category
            })
        }).then(res => res.json())
            .then(data => {
                toast.success(data.message)
            }).catch((error) => {
                console.log(error);
            })

    }
    const uploadPhoto = () => {
        const data = new FormData()
        data.append("file", image)
        data.append("upload_preset", "hridesh99!")
        data.append("cloud_name", "draowpiml")
        fetch('https://api.cloudinary.com/v1_1/draowpiml/image/upload', {
            method: 'post',
            body: data
        }).then(res => res.json())
            .then(data => {
                setPhoto(data.url);
            })
            .catch(err => toast.error(err))
    }

    const [image, setImage] = useState("");
    const [photo, setPhoto] = useState("");
    const [name, setName] = useState("");
    const [fatherName, setFatherName] = useState("");
    const [motherName, setMotherName] = useState("");
    const [gender, setGender] = useState("");
    const [address, setAddress] = useState("");
    const [mobileNumber, setMobileNumber] = useState("");
    const [dob, setDob] = useState("");
    const [course, setCourse] = useState("");
    const [category, setCategory] = useState("");
    return (
        <>
            {/* <AdmissionHeader/> */}
            <AdmissionBody />
            {/* <AdmissionFooter/> */}

        </>
    )
})
export default AdmissionForm;
