import React, { useEffect, useState } from 'react';

const YourComponent = () => {
  const [isScriptLoaded, setScriptLoaded] = useState(false);

  useEffect(() => {
    const loadTypedScript = async () => {
      // Create a script element
      const script = document.createElement('script');
      script.src = 'https://cdn.jsdelivr.net/npm/typed.js@2.1.0';
      script.async = true;

      // Promisify script loading
      const loadScript = () => new Promise((resolve, reject) => {
        script.onload = resolve;
        script.onerror = reject;
      });

      // Append the script to the head and wait for it to load
      document.head.appendChild(script);
      await loadScript();

      // Set the state to indicate that the script has loaded
      setScriptLoaded(true);
    };

    // Load Typed.js script
    loadTypedScript();

    return () => {
      // Cleanup function to clear the content if the script has loaded
      if (isScriptLoaded) {
        setScriptLoaded(false);
      }
    };
  }, [isScriptLoaded]);

  return (
    <div>
      {/* Element to contain animated typing */}
      {isScriptLoaded && <span id="element"></span>}
    </div>
  );
};

export default YourComponent;
