import React, { useState, useEffect } from 'react';

const YourComponent = () => {
    const [checkboxChecked, setCheckboxChecked] = useState(false);
    const [reCaptchaCompleted, setReCaptchaCompleted] = useState(false);
    const [submit, setSubmit] = useState(false);
    const [message, setMessage] = useState('');

    useEffect(() => {
        // Make sure to include your onloadCallback function here
        window.onloadCallback = function () {
            window.grecaptcha.render('g-recaptcha', {
                'sitekey': '6LeSkxwpAAAAAJr0__9WFMn2k3bJ9EW1eT52aaqm',
                'callback': function () {
                    setReCaptchaCompleted(true);
                }
            });
        };
    }, []);

    const postForm = () => {
        // Add your logic to submit the form data
        // For example, you can make an API call here

        // Update state variables accordingly
        setSubmit(true);
        setMessage('Submitted Successfully');
    };

    return (
        <div className="col-12 px-1 py-3 my-4"  id="declarationBg">
            <div className="form-check">
                <div className="bg-success-subtle text-danger p-2 rounded rounded-pill d-inline">
                    <label className="form-check-label h5" htmlFor="invalidCheck">
                        <div className="g-recaptcha" id="g-recaptcha" data-sitekey="6LeSkxwpAAAAAJr0__9WFMn2k3bJ9EW1eT52aaqm"></div>

                        <input
                            type="checkbox"
                            className="validCheck"
                            required
                            onChange={(e) => setCheckboxChecked(e.target.checked)}
                        />
                        <span className="icon"> </span>
                        <span style={{ fontSize: '1.2rem !important' }}>Declaration</span>
                    </label>
                </div>
            </div>
            <div className="col-12 ms-2">
                {submit ? (
                    <button className="btn btn-succuss ms-3" id="checkBtn" type="button">
                        Submitted Successfully
                    </button>
                ) : (
                    <button
                        className="btn btn-primary ms-3"
                        id="checkBtn"
                        type="button"
                        onClick={() => {
                            if (checkboxChecked && reCaptchaCompleted) {
                                postForm();
                            }
                        }}
                        disabled={!checkboxChecked || !reCaptchaCompleted}
                    >
                        Submit
                    </button>
                )}
                <div className="text-danger d-inline px-1">{message}</div>
            </div>
        </div>
    );
};

export default YourComponent;
