import React, { useState, useEffect, useRef } from 'react';

const SimpleCaptcha = () => {
  const [userInput, setUserInput] = useState('');
  const [captchaText, setCaptchaText] = useState(generateCaptcha());
  const [isCaptchaValid, setIsCaptchaValid] = useState(false);
  const inputRef = useRef(null);

  useEffect(() => {
    // Focus on the input element when the component mounts
    inputRef.current.focus();
  }, []); // Empty dependency array ensures that this effect runs only once, similar to componentDidMount

  function generateCaptcha() {
    // Generate a random string for the CAPTCHA
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    let captcha = '';
    for (let i = 0; i < 6; i++) {
      captcha += characters.charAt(Math.floor(Math.random() * characters.length));
    }
    return captcha;
  }

  const handleChange = (event) => {
    setUserInput(event.target.value);
  };

  const handleRefresh = () => {
    // Regenerate the CAPTCHA text
    setCaptchaText(generateCaptcha());
    // Clear user input
    setUserInput('');
    // Reset validation status
    setIsCaptchaValid(false);
    // Focus on the input element after refreshing
    inputRef.current.focus();
  };

  const handleSubmit = () => {
    // Check if the user input matches the CAPTCHA text
    setIsCaptchaValid(userInput === captchaText);

    // Your form submission logic goes here
    if (userInput === captchaText) {
      console.log('Form submitted successfully!');
      // Add your form submission logic here
    } else {
      console.log('CAPTCHA validation failed. Please try again.');
    }
  };

  return (
    <div className='mt-5 py-5'>
      {/* CAPTCHA display */}
      <div>
        <span>{captchaText}</span>
        <button type="button" onClick={handleRefresh}>
          Refresh
        </button>
      </div>

      {/* User input */}
      <div>
        <label htmlFor="captchaInput">Enter CAPTCHA: </label>
        <input
          type="text"
          id="captchaInput"
          value={userInput}
          onChange={handleChange}
          ref={inputRef} // Set the ref to the input element
        />
      </div>

      {/* Validation message */}
      {isCaptchaValid ? (
        <div style={{ color: 'green' }}>CAPTCHA is valid!</div>
      ) : (
        <div style={{ color: 'red' }}>CAPTCHA validation failed.</div>
      )}

      {/* Submit button */}
      <button type="button" onClick={handleSubmit}>
        Submit
      </button>
    </div>
  );
};

export default SimpleCaptcha;
